import React, { Fragment, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import styles from "../styles/PlaceDetail.module.scss";
import { Header } from "components/widgets";
import { useParams } from "react-router-dom";
import { useCustomState } from "state/state";
import Layout from "components/layouts/Layout/Layout";
import THEME from "state/theme";
import axios from "axios";

export default () => {
  const state = useCustomState()[0];
  const { contentid } = useParams();
  const [place, setPlace] = useState([]);

  const category = state.data.categories.filter(
    (cat) => cat.id === place.contenttypeid.toString()
  )[0];

  useEffect(()=> {
    axios.get(`/place/dtl/${contentid}`)
    .then((resp)=>{
      setPlace(resp.data)
    })
    .catch(()=>{

    })
  })



  return (
    <Fragment>
      <Header img={place.firstimage}>{place.title}</Header>

      <Layout col="1">
        <div className={styles.breadcrumbs} style={{ color: THEME.color }}>
          <Link to="/blog-grid-left-sidebar">Blog</Link>
          <i className="las la-long-arrow-alt-right" />
          <Link to={"/blog/cats/" + place.contenttypeid}>{category.title}</Link>
          <i className="las la-long-arrow-alt-right" />
          <span>{place.title}</span>
        </div>
      </Layout>

      <Layout col="2" padding>
        <div>
          <figure
            className={styles.image}
            style={{ background: "url(" + place.firstimage + ") center/cover" }}
          />
        </div>

      </Layout>


      <Layout col="2">
        {relatedPosts[0]}
        {relatedPosts[1]}
      </Layout>
    </Fragment>
  );
};
