import React, { Fragment, useState } from "react";
import { Header } from "components/widgets";
import { useCustomState } from "state/state";
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import ListSubheader from '@material-ui/core/ListSubheader';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import axios from "axios";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    width: '30ch',
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '30ch',
    },
  },
}));

export default () => {
  const state = useCustomState()[0];
  const classes = useStyles();

  const [addr1, setAddr1] = useState('')
  const [addr2, setAddr2] = useState('')
  const [areacode, setAreacode] = useState('')
  const [cat1, setCat1] = useState('')
  const [cat2, setCat2] = useState('')
  const [cat3, setCat3] = useState('')
  const [contentid, setContentid] = useState('')
  const [contenttypeid, setContenttypeid] = useState('')
  const [createdtime, setCreatedtime] = useState('')
  const [firstimage, setFirstimage] = useState('')
  const [firstimage2, setFirstimage2] = useState('')
  const [mapx, setMapx] = useState('')
  const [mapy, setMapy] = useState('')
  const [mlevel, setMlevel] = useState('')
  const [modifiedtime, setModifiedtime] = useState('')
  const [sigungucode, setSigungucode] = useState('')
  const [tel, setTel] = useState('')
  const [title, setTitle] = useState('')
  const [zipcode, setZipcode] = useState('')

  const save = e => {
    e.preventDefault()
    axios.post(`/place/save`, {
      addr1, addr2, areacode, cat1, cat2, cat3, contentid, contenttypeid, createdtime, firstimage,
      firstimage2, mapx, mapy, mlevel, modifiedtime, sigungucode, tel, title, zipcode,
      proxy: {
        host: 'localhost',
        port: 8080,
        protocol: 'http'
      }
    })
    .then(resp => {
      alert('저장 성공')
    })
    .catch(err => {
      alert('저장 실패')
    })
  }

  return (
    <Fragment>
      <Header img={state.data.header_bgs.contacts}>관광지 개별입력</Header>
      <div>
      <FormControl className={classes.formControl} name="cat1" onChange = { e => { setCat1(`${e.target.value}`) }}>
        <InputLabel htmlFor="grouped-native-select">대분류 (cat1)</InputLabel>
        <Select  native defaultValue="">
          <option aria-label="None" value="" />
          <optgroup label="Category 1">
            <option value={1}>A01</option>
            <option value={2}>A02</option>
            <option value={3}>A03</option>
            <option value={4}>A04</option>
            <option value={5}>A05</option>
          </optgroup>
          <optgroup label="Category 2">
            <option value={6}>B01</option>
            <option value={7}>B02</option>
          </optgroup>
          <optgroup label="Category 3">
            <option value={8}>C01</option>
          </optgroup>
        </Select>
      </FormControl>
      <FormControl className={classes.formControl} name="cat2" onChange = { e => { setCat2(`${e.target.value}`) }}>
        <InputLabel htmlFor="grouped-select">중분류 (cat2)</InputLabel>
        <Select defaultValue="">
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <ListSubheader>Category 1</ListSubheader>
          <MenuItem value={1}>A0101</MenuItem>
          <MenuItem value={1}>A0102</MenuItem>
          <MenuItem value={2}>A0201</MenuItem>
          <MenuItem value={2}>A0202</MenuItem>
          <MenuItem value={2}>A0203</MenuItem>
          <MenuItem value={2}>A0204</MenuItem>
          <MenuItem value={2}>A0205</MenuItem>
          <MenuItem value={2}>A0206</MenuItem>
          <MenuItem value={2}>A0207</MenuItem>
          <MenuItem value={2}>A0208</MenuItem>
          <MenuItem value={3}>A0301</MenuItem>
          <MenuItem value={3}>A0302</MenuItem>
          <MenuItem value={3}>A0303</MenuItem>
          <MenuItem value={3}>A0304</MenuItem>
          <MenuItem value={3}>A0305</MenuItem>
          <MenuItem value={4}>A0401</MenuItem>
          <MenuItem value={5}>A0501</MenuItem>
          <MenuItem value={5}>A0502</MenuItem>
          <ListSubheader>Category 2</ListSubheader>
          <MenuItem value={6}>B0101</MenuItem>
          <MenuItem value={6}>B0102</MenuItem>
          <MenuItem value={7}>B0201</MenuItem>
          <ListSubheader>Category 3</ListSubheader>
          <MenuItem value={8}>C0101</MenuItem>
          <MenuItem value={8}>C0102</MenuItem>
          <MenuItem value={8}>C0103</MenuItem>
          <MenuItem value={8}>C0104</MenuItem>
          <MenuItem value={8}>C0105</MenuItem>
          <MenuItem value={8}>C0106</MenuItem>
          <MenuItem value={8}>C0107</MenuItem>
          <MenuItem value={8}>C0108</MenuItem>
          <MenuItem value={8}>C0109</MenuItem>
          <MenuItem value={8}>C0110</MenuItem>
          <MenuItem value={8}>C0111</MenuItem>
          <MenuItem value={8}>C0112</MenuItem>
          <MenuItem value={8}>C0113</MenuItem>
          <MenuItem value={8}>C0114</MenuItem>
          <MenuItem value={8}>C0115</MenuItem>
          <MenuItem value={8}>C0116</MenuItem>
          <MenuItem value={8}>C0117</MenuItem>
        </Select>
      </FormControl>
      <FormControl className={classes.formControl} name="cat3" onChange = { e => { setCat3(`${e.target.value}`) }}>
        <InputLabel htmlFor="grouped-select">소분류 (cat3)</InputLabel>
        <Select defaultValue="">
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <ListSubheader>Category 1</ListSubheader>
        </Select>
      </FormControl>    
      <form className={classes.root} noValidate autoComplete="off">
      <TextField name="addr1" style={{ fontSize: '63px' }} label="addr1" defaultValue="addr1" onChange = { e => { setAddr1(`${e.target.value}`) }}/>
      <TextField name="addr2" label="addr2" defaultValue="addr2" onChange = { e => { setAddr2(`${e.target.value}`) }}/>
      <TextField name="areacode"label="areacode" defaultValue="areacode" onChange = { e => { setAreacode(`${e.target.value}`) }}/>
      <TextField name="contentid" label="contentid" defaultValue="contentid" onChange = { e => { setContentid(`${e.target.value}`) }}/>
      <TextField name="contenttypeid"label="contenttypeid" defaultValue="contenttypeid" onChange = { e => { setContenttypeid(`${e.target.value}`) }}/>
      <TextField name="createdtime"label="createdtime" defaultValue="createdtime" onChange = { e => { setCreatedtime(`${e.target.value}`) }}/>
      <TextField name="firstimage"label="firstimage" defaultValue="firstimage" onChange = { e => { setFirstimage(`${e.target.value}`) }}/>
      <TextField name="firstimage2" label="firstimage2" defaultValue="firstimage2" onChange = { e => { setFirstimage2(`${e.target.value}`) }}/>
      <TextField name="mapx" label="mapx" defaultValue="mapx" onChange = { e => { setMapx(`${e.target.value}`) }}/>
      <TextField name="mapy" label="mapy" defaultValue="mapy" onChange = { e => { setMapy(`${e.target.value}`) }}/>
      <TextField name="mlevel" label="mlevel" defaultValue="mlevel" onChange = { e => { setMlevel(`${e.target.value}`) }}/>
      <TextField name="modifiedtime" label="modifiedtime" defaultValue="modifiedtime" onChange = { e => { setModifiedtime(`${e.target.value}`) }}/>
      <TextField name="sigungucode" label="sigungucode" defaultValue="sigungucode" onChange = { e => { setSigungucode(`${e.target.value}`) }}/>
      <TextField name="Retelquired" label="Retelquired" defaultValue="tel" onChange = { e => { setTel(`${e.target.value}`) }}/>
      <TextField name="title" label="title" defaultValue="title" onChange = { e => { setTitle(`${e.target.value}`) }}/>
      <TextField name="zipcode" label="zipcode" defaultValue="zipcode" onChange = { e => { setZipcode(`${e.target.value}`) }}/>
      </form>
      </div>
      <Button variant="outlined" color="primary" type="submit" onClick={save}>
        저장
      </Button>
      <Button variant="outlined" color="primary">
        취소
      </Button>
    </Fragment>
  );
};
