import React, { useState, useEffect, useCallback, Fragment } from "react";
import styles from "../styles/PlaceList.module.scss";
import { useCustomState } from "state/state";
import { useParams, useRouteMatch } from "react-router-dom";
import { animateScroll as scroll } from "react-scroll";
import Button from 'components/elements/Button/Button'

import {
  BlogCard,
  BlogPagination,
  BlogCategories,
  BlogFeatured,
  BlogSearch,
} from "components/pages/Blog/components";

import { Header } from "components/widgets";
import axios from "axios";

export default ({ sidebar = "left", layout = "grid", title = "title" }) => {
  const state = useCustomState()[0];
  const { category, author, posting_date, query } = useParams();
  const { url } = useRouteMatch();

  const [currentPage, setCurrentPage] = useState(1);
  const postsPerPage = useState(6)[0];
  const [postsArray, setPostsArray] = useState([]);
  const [pageTitle, setPageTitle] = useState(title);
  const [placeList, setPlaceList] = useState([])

  const filterPosts = useCallback(() => {
    let posts = [...state.data.posts];

    if (category) {
      posts = state.data.posts.filter(
        (post) => category === post.category_id.toString()
      );
      setPageTitle(
        "Category: " +
          state.data.categories.filter((cat) => cat.id === category)[0].title
      );
    } else if (author) {
      posts = state.data.posts.filter(
        (post) => author === post.user_id.toString()
      );
      setPageTitle(
        "Author: " +
          state.data.users.filter((user) => user.id.toString() === author)[0]
            .name
      );
    } else if (posting_date) {
      posts = state.data.posts.filter(
        (post) => posting_date === post.posting_date
      );
      setPageTitle("Date: " + posting_date);
    } else if (query) {
      posts = state.data.posts.filter((post) => post.title.includes(query));
      setPageTitle("Search for: " + query);
    } else {
      setPageTitle(title);
    }

    return posts;
  }, [
    author,
    category,
    posting_date,
    query,
    state.data.categories,
    state.data.posts,
    state.data.users,
    title,
  ]);
  const URL = '/place/all'
  useEffect(() => {
    axios.get(URL, )
    .then((resp) => {
      setPlaceList(resp.data)
    })
    .then(re)
    .catch((err) => {
      throw err;
    })


    setPostsArray(
      filterPosts().map((post, index) => {
        return (
          <BlogCard
            key={index}
            layout={layout}
            post={post}
            author={
              state.data.users.filter(
                (user) => user.id.toString() === post.user_id
              )[0]
            }
            category={
              state.data.categories.filter(
                (cat) => cat.id === post.category_id.toString()
              )[0].title
            }
          />
        );
      })
    );
    setCurrentPage(1);
  }, [
    layout,
    category,
    author,
    query,
    posting_date,
    url,
    state.data.users,
    state.data.categories,
    filterPosts,
  ]);

  return (
    <Fragment>
      <Header img={state.data.header_bgs.blog}>{pageTitle}</Header>
      <section
        className={[
          styles.wrapper,
          sidebar === "left"
            ? styles.with_sidebar + " " + styles.left
            : sidebar === "right"
            ? styles.with_sidebar + " " + styles.right
            : null,
        ].join(" ")}
      >
        <aside className={styles.sidebar}>
          <BlogSearch />
          <BlogCategories data={state.data.categories} />
          <BlogFeatured data={state.data.posts} />
          <Button to={'/place/add'}>{"관광지 추가"}</Button>
        </aside>

        <article>
          <div
            className={
              layout === "grid"
                ? styles.grid
                : layout === "list"
                ? styles.list
                : null
            }
          >
            {postsArray.slice(
              (currentPage - 1) * postsPerPage,
              currentPage * postsPerPage
            )}
          </div>

          <BlogPagination
            amount={Math.ceil(postsArray.length / postsPerPage)}
            current={currentPage}
            next={() => {
              setCurrentPage((c) => c + 1);
              scroll.scrollToTop();
            }}
            prev={() => {
              setCurrentPage((c) => c - 1);
              scroll.scrollToTop();
            }}
            goto={(id) => {
              setCurrentPage(id);
              scroll.scrollToTop();
            }}
          />
        </article>
      </section>
    </Fragment>
  );
};
