import React, { Fragment, useState } from "react";
import { Header } from "components/widgets";
import { useCustomState } from "state/state";
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import axios from "axios";
import { PlaceSharp } from "@material-ui/icons";

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '30ch',
    },
  },
  margin: {
    margin: theme.spacing(1),
  },
}));

export default () => {
  const state = useCustomState()[0];
  const classes = useStyles();

  const [contentid, setContentid] = useState('')
  const [overview, setOverview] = useState('')

  const save = e => {
    e.preventDefault()
    axios.post(`/detail/save`, {
      contentid, overview,
      proxy: {
        host: 'localhost',
        port: 8080,
        protocol: 'http'
      }
    })
    .then(resp => {
      alert('저장 성공')
    })
    .catch(err => {
      alert('저장 실패')
    })
  }
  const saveOverview = e => {
    e.preventDefault()
    const places = resp.response.body.items.item
    places.map((elem) => {
      const contentid = elem.contentid
      const url = `http://api.visitkorea.or.kr/openapi/service/rest/KorService/detailCommon?ServiceKey=7hb2dIRIDTkLxtu%2B4LFWyzBfvlvHKUirbLFOQRPT%2Fn%2FP514YgScrPtXK%2B1ErcLcnroGa0Uj8%2B%2FM%2BY5x1CWROaw%3D%3D&contentId=${contentid}&defaultYN=N&addrinfoYN=N&overviewYN=Y&MobileOS=ETC&MobileApp=AppTest&_type=json`
      fetch(URL, )
      .then((resp) => {
        alert(`저장완료`)
        return resp.json()
      })
      .then(resp => {
        axios.post('/detail/save', places)
        .then()
        .catch()
      })
      .catch((err) => {
        alert(`저장실패`)
        ㅅ
      })
    })
  }

  return (
    <Fragment>
      <Header img={state.data.header_bgs.contacts}>관광지 개요 입력</Header>
      <div>  
      <form className={classes.root} noValidate autoComplete="off">
      <TextField name="contentid" label="contentid" placeholder="contentid" onChange = { e => { setContentid(`${e.target.value}`) }}/>
      <TextField name="overview" style={{ fontSize: '80px' }} label="overview" placeholder="overview" onChange = { e => { setOverview(`${e.target.value}`) }}/>
      </form>
      </div>
      <Button 
          variant="outlined" 
          color="primary" 
          type="submit" 
          onClick={save}
          className={classes.margin}>
        저장
      </Button>
      <Button 
          variant="outlined" 
          color="primary" 
          type="button" 
          href='/place/list'
          className={classes.margin}>
        취소
      </Button>
      <Button
          variant="outlined"
          color="primary"
          type="submit" onClick={saveOverview}
          className={classes.margin}
        >Tour Api 개요 가져오기</Button>
    </Fragment>
  );
};
